import React, {Component} from 'react';
import { Card, Button } from 'semantic-ui-react';
import factory from '../ethereum/factory';
import Layout from '../components/layout';
import { Link } from '../routes';
import fetch from 'cross-fetch';
// import jsonArray from '../localmodules/data_initialization';

class App extends Component {

    static async getInitialProps(props) {
        const shops = await factory.methods.getDeployedShops().call();
        //const headerToken = props.query.headerToken;
        return { shops}//, headerToken };
    }

    renderShops() {
        const items = this.props.shops.map(address => {
            return{
                header : address,
                description : <Link route={`shops/${address}`}><a>View Shops</a></Link>,
                fluid : true
            };
        });

        return <Card.Group items={items}/>;
    }

    // onClick = async (event) => {
    //     event.preventDefault();
    //     const options = {
    //         method : 'get',
    //         headers: { 
    //             'Content-Type':'application/json',
    //             'x-auth-token' : this.props.headerToken
    //         },
    //     };


    //     const res = await fetch('http://localhost:7800/user/loign/me', options);
    //     const status = await res.status;
    //     const data = await JSON.stringify(res);
    //     console(data);
    // }

    render() {
        return (
            <Layout>
                <div>
                    {this.renderShops()}
                    <Button
                        //onClick={(event) => this.onClick}
                        content="Create Shops"
                        icon="add circle"
                        primary
                    />
                </div>
                <div>{this.props}</div>
            </Layout>
        );

    }

}

export default App;
