import React, {Component} from 'react';
import validate from '../../localmodules/token_validate';
import get from '../../localmodules/express_get';
import { Card, Grid, Feed } from 'semantic-ui-react';
import Layout from '../../components/layout';

export default class UserDetails extends Component {

    state = {
        res : null
    }
    static getInitialProps(props) {
        return validate(props.query.headerToken);
    }
    componentDidMount = async () => {
        const headers = { 'Content-Type':'application/json', 'x-auth-token' : this.props.headerToken};
        let res;
        if(this.props.loggedIn){
            res = await get('/user/login/me', headers);
            console.log(res);
            this.setState({ res })
        }
    }

    renderEachCard = () => {
        return (
            <Card.Content>
                <Card.Header>Details</Card.Header>
                <Card.Description>This is a descriptive information that is a must watch.This is a descriptive information that is a must watch.This is a descriptive information that is a must watch.This is a descriptive information that is a must watch.This is a descriptive information that is a must watch.This is a descriptive information that is a must watch.This is a descriptive information that is a must watch.This is a descriptive information that is a must watch.This is a descriptive information that is a must watch.This is a descriptive information that is a must watch.This is a descriptive information that is a must watch.</Card.Description>
                <Card.Meta>Description</Card.Meta>
            </Card.Content>
        );
    }

    renderCard = () => {
        return (
            <Grid>
                <Grid.Column width={4}>
                    <Grid.Row>
                        <Card fluid style={{marginBottom : '30px'}}>
                            <Card.Content>
                                <Feed>
                                    <Feed.Event>
                                        <Feed.Label image='https://img1a.flixcart.com/www/linchpin/fk-cp-zion/img/profile-pic-male_2fd3e8.svg'/>
                                    </Feed.Event>
                                </Feed>
                                <Card.Meta>Hello,</Card.Meta>
                                <Card.Header>{`${this.state.res.data.name.first} ${this.state.res.data.name.last}`}</Card.Header>
                            </Card.Content>
                        </Card>
                    </Grid.Row>
                    <Grid.Row>
                        <Card fluid>
                            {this.renderEachCard()}
                        </Card>
                    </Grid.Row>
                </Grid.Column>
                <Grid.Column width={12}>
                    <Card fluid>
                        {this.renderEachCard()}
                        {this.renderEachCard()}
                        {this.renderEachCard()}
                        {this.renderEachCard()}
                        {this.renderEachCard()}
                        {this.renderEachCard()}
                    </Card>
                </Grid.Column>
            </Grid>
        );
    }

    render(){
        return <Layout>{this.renderCard()}</Layout> 
    }



}
