import React, { Component } from 'react';
import { Menu, Container, Segment } from 'semantic-ui-react';
import {Link, Router} from '../routes';
import { render } from 'react-dom';

export default class Navbar extends Component {

    state = {
        activeItem:'home'
    };

    static getInitialProps(props) {
        return { loggedIn };
    }

    onClick = (event, { name })=> {
        this.setState({activeItem:name});
        console.log(name);
    }

    render() {

        const { activeItem } = this.state;

        let v;
        if(this.props.loggedIn == true) { 
            v = 'Aakarsh';
        } else {
            v = 'Login';
        }

        return (
                <Menu size='massive' inverted color='blue'>
                    <Link route='/'>
                        <a className='item'>eR</a>
                    </Link>
                    <Container>
                        <Menu.Item 
                            name='home'
                            active={activeItem === 'home'}
                            onClick={this.onClick}
                        />

                        <Menu.Item 
                            name='something1'
                            active={activeItem === 'something1'}
                            onClick={this.onClick}
                        />

                        <Menu.Item 
                            name='something2'
                            active={activeItem === 'something2'}
                            onClick={this.onClick}
                        />
                    </Container>

                    <Menu.Menu position="right">

                        <Link route='/user/register'>
                            <a className='item'>Register</a>
                        </Link>

                        <Link route='/user/login'>
                            <a className='item'>{v}</a>
                        </Link>

                    </Menu.Menu>
                </Menu>
        );
    }
}
