import React, {Component} from 'react';
import {Comment,Header, Form, Button} from 'semantic-ui-react';
import postWithData from '../localmodules/express_post_data';

export default class CommentExampleComment extends Component {



    getInit = async () =>{
        console.log( this.props.fpdName );
        const dataInp = {
            fpdName : this.props.fpdName
        }
        const headers = { 'Content-Type':'application/json'};
        const {data, status} = await postWithData('/complaints/list', headers, dataInp)
        return { data : data['record'] , status };
    }

    constructor(props){
        super(props);
        const { data, status } = this.getInit();
        console.log(data);
        this.state = {
            currentComment : '',
            data,
            status
        }
    }

    postComplaint = (event) =>{
        event.preventDefault();
    }

    render() {
        const data = ['a', 'b', 'c'];
        const comments = data.map((item, index) => {
            return (
                <Comment>
                    <Comment.Content>
                    <Comment.Author as='a'>Matt</Comment.Author>
                    <Comment.Metadata>
                        <div>Today at 5:42PM</div>
                    </Comment.Metadata>
                    <Comment.Text>How artistic!</Comment.Text>
                    <Comment.Actions>
                        <Comment.Action>Reply</Comment.Action>
                    </Comment.Actions>
                    </Comment.Content>
                </Comment>
            );
        });

        return (
            <Comment.Group>
            <Header as='h3' dividing>
                Complaints
            </Header>

            {comments}

            <Form reply onSubmit={this.postComplaint}>
                <Form.TextArea 
                    value={this.state.currentComment}
                    onChange = {(e) => this.setState({currentComment : e.target.value})}
                />
                <Button content='Add Reply' labelPosition='left' icon='edit' primary />
            </Form>
            </Comment.Group>
        );
    }
}
