import React, { Component } from 'react';
import Layout from '../../components/layout';
import Shop from '../../ethereum/shop';
import { Card, Table } from 'semantic-ui-react';


class ShopShow extends Component {

    static async getInitialProps(props) {
        const address = props.query.address;
        console.log(address);
        const s = Shop(address);
        const details = await s.methods.getDetails().call();
        console.log(details);
        return { 
            managerName : details[0],
            rice : details[1],
            wheat : details[2],
            arhad : details[3],
            fpdName : details[4],
            shopName : details[5],
            manager : details[6],
            addr : details[7]
        };
    }

    renderTable() {
        return (
            <Table>
                <Table.Header>
                    <Table.Row>
                        <Table.Cell>Name</Table.Cell>
                        <Table.Cell>Place</Table.Cell>
                        <Table.Cell>Animal</Table.Cell>
                        <Table.Cell>Thing</Table.Cell>
                        <Table.Cell>Cell</Table.Cell>
                    </Table.Row>
                </Table.Header>


                <Table.Body>
                    <Table.Row>
                        <Table.Cell>Aakarsh</Table.Cell>
                        <Table.Cell>Patna</Table.Cell>
                        <Table.Cell>Tiger</Table.Cell>
                        <Table.Cell>Lamp</Table.Cell>
                        <Table.Cell>First</Table.Cell>
                    </Table.Row>
                </Table.Body>

            </Table>





        );
    }

    renderCard() {
        const items = [
            {
                header : this.props.shopName,
                description : 'SHOP NAME',
                meta : 'This is the name of the shop.'
            },
            {
                header : this.props.fpdName,
                description : 'FDP NUMBER',
                meta : 'This is the name of the shop.'
            },
            {
                header : this.props.managerName,
                description : 'MANAGER NAME',
                meta : 'This is the name of the shop.'
            },
            {
                header : this.props.addr,
                description : 'SHOP ADDRESS',
                meta : 'This is the name of the shop.'
            },
            {
                header : this.props.rice,
                description : 'RICE STOCK',
                meta : 'This is the name of the shop.'
            },
            {
                header : this.props.wheat,
                description : 'WHEAT STOCK',
                meta : 'This is the name of the shop.'
            },
            {
                header : this.props.arhad,
                description : 'ARHAD STOCK',
                meta : 'This is the name of the shop.'
            },
        ];

        return <Card.Group items = {items} />;
    }


    render() {
        return (
            <Layout>
                {this.renderCard()}
            </Layout>
        );
    }
}


export default ShopShow;
