import React, {Component} from 'react';
import { Button, Form, Grid, Header, Image, Message, Segment, Input, GridColumn } from 'semantic-ui-react';
import Layout from '../../components/layout.js'
import { Link } from '../../routes';


class RegisterForm extends Component {

    state = {
        address : {
            pin :'',
            house:'',
            area:'',
            district:'',
            state:'',
        },
        firstName : '',
        middleName : '',
        lastName : ''
    };

    onSubmit = event => {
        event.preventDefault();
        console.log(this.state.firstName);
    }


    render() {
        return (
            <Layout>
                <Grid verticalAlign='middle' >
                    <Grid.Column width={2}></Grid.Column>
                    <Grid.Column width={12}>
                        <Header as='h2' color='blue' textAlign='center' style={{margin : '35px 35px'}}>
                            Register a new account
                        </Header>
                        <Form onSubmit={this.onSubmit}>
                            <Form.Group widths='equal'>
                                <Form.Field>
                                    <Input
                                        label = 'First Name'
                                        placeholder = 'First Name'
                                        value={this.state.firstName}
                                        onChange={event => this.setState({ firstName: event.target.value })}
                                    />
                                </Form.Field>
                                <Form.Field>
                                    <label>Middle Name</label>
                                    <Input
                                        placeholder = 'Middle Name'
                                        value={this.state.middleName}
                                        onChange={event => this.setState({ middleName: event.target.value })}
                                    />
                                </Form.Field>
                                <Form.Field>
                                    <label>Last Name</label>
                                    <Input
                                        placeholder = 'Last Name'
                                        value={this.state.lastName}
                                        onChange={event => this.setState({ lastName: event.target.value })}
                                    />
                                </Form.Field>
                            </Form.Group>
                            <Form.Group widths='equal'>
                                <Form.Field>
                                    <label>Address</label>
                                    <Input
                                        placeholder = 'House'
                                        value={this.state.address.house}
                                        onChange={event => this.setState({ address : { house : event.target.value }})}
                                    />
                                </Form.Field>
                            </Form.Group>
                            <Button primary>Sumbit</Button>
                        </Form>
                    </Grid.Column>
                    <Grid.Column width={2}></Grid.Column>
                </Grid>
            </Layout>
        );
    }
}

export default RegisterForm;
