const factory = require('../ethereum/factory');
const web3 = require('../ethereum/web3');
const Shop = require('../ethereum/shop');

const saveToEthereum = async (obj) => {
  const accounts = await web3.eth.getAccounts();
  console.log(accounts, obj);
  // await factory.methods
  //   .createShop(obj.managerName, obj.rice, obj.wheat, obj.arhad, obj.kerosene, obj.fpdName, obj.shopName, obj.addr)
  //   .send({
  //     from: accounts[0],
  //   });

  const addresses = await factory.methods.getDeployedShops().call();
  console.log(addresses);
  const s = Shop(addresses[-1]);
  await s.methods.setBlockName(obj.blockName).send({ from : accounts[0]});
}
module.exports = saveToEthereum;
