import React, { Component } from 'react';
import { Form, Input, Button } from 'semantic-ui-react';
import saveToEthereum from '../../localmodules/data_initialization';


export default class UpdateBlockChain extends Component {

    state = {
        managerName : '',
        fpdName : '',
        shopName : '',
        rice : '',
        wheat : '',
        arhad : '',
        addr : ''
    }

    onSubmit = () => {
        const obj = this.state;
        console.log(obj);
    }


    render() {
        return (
            <div>
                <Form onSubmit={this.onSubmit}>
                    <Form.Field>
                        <label>Manager Name</label>
                        <Input
                            placeholder='Manager Name'
                            type='text'
                            value={this.state.managerName}
                            onChange={ event => this.setState({ managerName : event.target.value }) }
                            />
                    </Form.Field>
                    <Form.Field>
                        <label>Shop Name</label>
                        <Input
                            placeholder='Shop Name'
                            type='text'
                            value={this.state.shopName}
                            onChange={ event => this.setState({ managerName : event.target.value }) }
                            />
                    </Form.Field>
                    <Form.Field>
                        <label>Address</label>
                        <Input
                            placeholder='Address'
                            type='text'
                            value={this.state.addr}
                            onChange={ event => this.setState({ managerName : event.target.value }) }
                            />
                    </Form.Field>
                    <Form.Field>
                        <label>FPD Name</label>
                        <Input
                            placeholder='FPD Name'
                            type='text'
                            value={this.state.fpdName}
                            onChange={ event => this.setState({ managerName : event.target.value }) }
                            />
                    </Form.Field>
                    <Form.Field>
                        <label>Rice</label>
                        <Input
                            placeholder='Rice'
                            type='number'
                            value={this.state.rice}
                            onChange={ event => this.setState({ managerName : event.target.value }) }
                            />
                    </Form.Field>
                    <Form.Field>
                        <label>Wheat</label>
                        <Input
                            placeholder='Wheat'
                            type='number'
                            value={this.state.wheat}
                            onChange={ event => this.setState({ managerName : event.target.value }) }
                            />
                    </Form.Field>
                    <Form.Field>
                        <label>Wheat</label>
                        <Input
                            placeholder='Wheat'
                            type='number'
                            value={this.state.arhad}
                            onChange={ event => this.setState({ managerName : event.target.value }) }
                            />
                    </Form.Field>
                    <Button>Submit</Button>
                </Form>
            </div>
        );
    }
}
