const Web3 = require('web3');

let web3;


const f = async () => {
  if (window.ethereum) {
    window.web3 = new Web3(ethereum);
    try {
        // Request account access if needed
        await ethereum.enable();
        // Acccounts now exposed
    } catch (error) {
        // User denied account access...
    }
}
}





const funct = async (web) => {
  const accounts = await web.eth.getAccounts();
  console.log('Accounts', accounts);
}

if (typeof window !== 'undefined' && typeof window.web3 !== 'undefined') {
  // We are in the browser and metamask is running.
  console.log(' We are in the browser and metamask is running.', window);
  web3 = new Web3(window.ethereum);
  funct(web3);
} else {
  // We are on the server *OR* the user is not running metamask
  console.log('We are on the server *OR* the user is not running metamask')
  const provider = new Web3.providers.HttpProvider(
    'https://rinkeby.infura.io/v3/42d235c7d6304509a63bec8b043c9bc2'
  );
  web3 = new Web3(provider);
  funct(web3);
}

module.exports = web3;
