import React, { Component } from 'react';
import Layout from '../../components/layout';
import Shop from '../../ethereum/shop';
import { Card, Table, Grid, Button } from 'semantic-ui-react';
import {  Link, Router } from '../../routes';
import validate from '../../localmodules/token_validate';


class ShopShow extends Component {

    static async getInitialProps(props) {
        const { headerToken, loggedIn } = validate(props.query.headerToken);
        const address = props.query.address;
        const s = Shop(address);
        const details = await s.methods.getDetails().call();
        const blockName = await s.methods.blockName().call();
        return { 
            managerName : details[0],
            items : [{quantity : details[1], name : 'Rice'},
                    {quantity: details[2], name : 'Wheat'},
                    {quantity : details[3], name : 'Arhad'},
                    {quantity : details[4], name : 'Kerosene'}],
            fpdName : details[5],
            shopName : details[6],
            manager : details[7],
            addr : details[8],
            currentAddress : address,
            headerToken,
            loggedIn,
            blockName
        };
    }

    renderTable() {
        const tableRows = this.props.items.map((item, index) => {
            return (
                <Table.Row key={index}>
                    <Table.Cell>{item.name}</Table.Cell>
                    <Table.Cell>{item.quantity}</Table.Cell>
                    <Table.Cell>{10}</Table.Cell>
                </Table.Row>
            );
        });
        return (
            <div>
                <h3><strong>STOCK STATEMENT</strong></h3>
                <Table>
                    <Table.Header>
                        <Table.Row>
                            <Table.HeaderCell>Item Name</Table.HeaderCell>
                            <Table.HeaderCell>Stock</Table.HeaderCell>
                            <Table.HeaderCell>Price(per KG)</Table.HeaderCell>
                        </Table.Row>
                    </Table.Header>
                    <Table.Body>
                        {tableRows}
                    </Table.Body>
                </Table>
            </div>
        );
    }

    renderCard() {
        const items = [
            {
                header : this.props.shopName,
                description : 'SHOP NAME',
                meta : 'This is the name of the shop.'
            },
            {
                header : this.props.fpdName,
                description : 'FDP NUMBER',
                meta : 'This is the name of the shop.'
            },
            {
                header : this.props.managerName,
                description : 'MANAGER NAME',
                meta : 'This is the name of the shop.'
            },
        ];
        const desc = [
            {       
                header : this.props.blockName,
                description : 'BLOCK NAME',
                meta : 'This is the name of the block.',
            },
            {       
                header : this.props.addr,
                description : 'SHOP ADDRESS',
                meta : 'This is the name of the shop.',
            },
        ];

        return (
            <div>
                <Card.Group items = {items} itemsPerRow = {3}/>
                <Card>
                    <Card.Content>
                        <Card.Header>{this.props.blockName}</Card.Header>
                        <Card.Meta>This is the name of the block.</Card.Meta>
                        <Card.Description>BLOCK NAME</Card.Description>
                    </Card.Content>
                </Card>
                <Card>
                    <Card.Content>
                        <Card.Header>{this.props.addr}</Card.Header>
                        <Card.Meta>SHOP ADDRESS</Card.Meta>
                        <Card.Description>BLOCK NAME</Card.Description>
                    </Card.Content>
                </Card>
            </div>
        );
    }

    renderButton(){
        return (
            <Link route={`/shops/${this.props.currentAddress}/payment`}>
                <a>
                    <Button color='blue'>
                     Make Payment
                    </Button>
                </a>
            </Link>
        );
    }


    render() {
        return (
            <Layout headerToken={this.props.headerToken} loggedIn={this.props.loggedIn}>
                <Grid style={{color:'#2185D0'}} columns={1}>
                    <Grid.Row>
                        <Grid.Column>
                            {this.renderCard()}
                        </Grid.Column>
                    </Grid.Row>
                    <Grid.Row textAlign='center'>
                        <Grid.Column>
                            {this.renderTable()}
                        </Grid.Column>  
                    </Grid.Row>
                    <Grid.Row textAlign='center'>
                        <Grid.Column>
                            {this.renderButton()}
                        </Grid.Column>  
                    </Grid.Row>
                </Grid>
            </Layout>
        );
    }
}


export default ShopShow;
