import React, { Component } from 'react';
import Layout from '../../components/layout';
import Shop from '../../ethereum/shop';
import { Card } from 'semantic-ui-react';


class ShopShow extends Component {

    static async getInitialProps(props) {
        const address = props.query.address;
        console.log(address);
        const s = Shop(address);
        const details = await s.methods.getDetails().call();
        console.log(details);
        return { 
            managerName : details[0],
            rice : details[0],
            wheat : details[1],
            arhad : details[2],
            fpdName : details[3],
            shopName : details[4],
            manager : details[5],
            addr : details[6]
        };
    }

    renderCard() {
        const items = [
            {
                header : 'Shop Name',
                description : this.props.shopName,
                meta : 'This is the name of the shop.'
            }
        ];

        return <Card.Group items = {items} />;
    }


    render() {
        return (
            <Layout>
                {this.renderCard()}
            </Layout>
        );
    }
}


export default ShopShow;
