import React, {Component} from 'react';
import { Card } from 'semantic-ui-react';
import factory from '../ethereum/factory';
// import jsonArray from '../localmodules/data_initialization';

class App extends Component {

    static async getInitialProps() {
        const shops = await factory.methods.getDeployedShops().call();
        console.log(shops);

        return { shops };
    }

    renderShops() {
        const items = this.props.shops.map(address => {
            return{
                header : address,
                description : '<a>View Shops</a>',
                fluid : true
            };
        });

        return <Card.Group items={items}/>;
    }

    render() {
        return <div>{this.renderShops()}</div>;
    }

}

export default App;
